function processCompanyData(data){let companies=[];return data.companies.forEach((function(company){let groupName=company.name.split(":")[0].trim(),companyId=company.id,companyName=company.name.split(":")[1].trim(),groupIndex=companies.findIndex((group=>group.text===groupName));-1===groupIndex?companies.push({text:groupName,children:[{id:companyId,text:companyName}]}):companies[groupIndex].children.push({id:companyId,text:companyName})})),companies}$(document).ready((function(){loadJSON("#companies_type","companyGUID.json","company")}));