document.addEventListener("DOMContentLoaded",(function(){const shortNameInput=document.getElementById("shortname"),nameInput=document.getElementById("name"),sameAsShortNameCheckbox=document.getElementById("sameAsShortName");nameInput.value=shortNameInput.value,sameAsShortNameCheckbox.addEventListener("change",(function(){this.checked?(nameInput.value=shortNameInput.value,nameInput.disabled=!0):nameInput.disabled=!1})),shortNameInput.addEventListener("input",(function(){sameAsShortNameCheckbox.checked&&(nameInput.value=this.value)}))}));